property.value:
import ptolemy.data.IntToken

// Groovy actor to compute factorials.

public class Actor {
    def fire() {
        int val = input.get(0).intValue()
        if(val < 0) {
            actor.error("Input must be non-negative.");
            return
        }
        int total = factorial(val)
        output.broadcast(new IntToken(total))
    }

    def factorial(n) { n == 0 ? 1 : n * factorial(n - 1) }
}
