property.value:
//
// Groovy actor demonstrating method interception.
//

public class Actor {

  // intercept calls to any method other than existing methods
  def invokeMethod(String name, args) {

    // ignore stopFire
    if(!name.equals("stopFire")) { 
        //println("in " + name)

        // append the name of the method to buf
        buf += " " + name
    }

    // if we're in wrapup(), check buf contents
    if(name.equals("wrapup")) {
        println(buf)
        assert buf.equals("preinitialize initialize prefire fire postfire wrapup")
    }
    return
  }

  // initialize the string buffer buf
  def preinitialize() {
      buf = "preinitialize"
  }

  def String buf = ""
}
