svg.script:
     function show(tid) {
         if (lastMsg != null) {
            lastMsg.setAttribute("visibility", "hidden");
         }
         obj = svgDocument.getElementById(tid);
         obj.setAttribute("visibility", "visible");
         obj.setAttribute("fill", "navy");
         obj = svgDocument.getElementById(tid+"Polygon");
         obj.setAttribute("fill", "#5f7f8f");
     }
     function unshow(tid) {
         obj = svgDocument.getElementById(tid);
         obj.setAttribute("visibility", "hidden");
         obj = svgDocument.getElementById(tid+"Polygon");
         obj.setAttribute("fill", "white");
         if (lastMsg != null) {
            lastMsg.setAttribute("fill", "brown");
            lastMsg.setAttribute("visibility", "visible");
         }
     }
     var lastCircle;
     var lastMsg;
