lengthTest <- function(fns,mincnt) {
 fnsBoolean = rep(TRUE,length(fns));
 for (i in 1:length(fns)) {
  cnt = length(readLines(fns[i]));
  fnsBoolean[i] = cnt>mincnt;
 }
 return(fnsBoolean)
}
